INSERT INTO `mall_goods` VALUES (10, 'CIO修炼之道 用IT为企业赋能 首席信息官信息主管信息化 IT 互联网+ 企业管理 数字转型 26位一线企业信息化工作者的工作笔记', '9603424362719', '杨彦武，景保玉 ', '人民邮电出版社', '2018-08-01 00:00:00', 1, '以科技创新促进企业转型升级26位一线企业信息化工作者的实战分享120多位500强企业、名企CIO的智慧结晶涉及企业包括*、苏宁、新浪、搜狐、映客直播、用友、中粮、首钢、一点资讯、陌陌、搜狗、万达、拜尔、中通快递等', 'images/goods/176a85114a1b4dd08570c2a1ac865642.jpg', 1042, 69.00, 64.70, '2019-04-13 22:34:15', NULL, 1);
