INSERT INTO `mall_goods` VALUES (13, 'IT精英职场日语Email', '2743567367263', '司炳月 编 ', '大连理工大学出版社', '2013-04-01 00:00:00', 1, '身在IT行业工作的您还在为IT行业的专业日语E-mail而犯憷发愁吗？别担心，这本汇集IT行业必用的E-mail大全书，将助你文思泉涌，从此轻松处理IT日语邮件不发愁！ 本书选材精准、内容丰富，涵盖了IT简历、IT求职信、职场生活、IT专业E-mail等四大方向。每封邮件都有多种范例，灵活多用。同时还附有核心单词解析，帮你在轻松搞定E-mail的同时将词汇一并加强巩固。 《IT精英职场日语E-mail》让您开启IT行业的华丽转身！', 'images/goods/211879b25a434710842af8b6a6cca196.jpg', 177, 30.00, 23.10, '2019-12-19 09:04:21', NULL, 1);
