INSERT INTO `mall_goods` VALUES (32, 'Word It优先顺序高考单词（按照近年来高考中出现单词的重要程度收录了高中学生必须掌握的词汇）', '8983506267182', '尹诚 编 ', '机械工业出版社', '2012-08-01 00:00:00', 1, 'Word It优先顺序高考单词（便携本） Word It优先顺序中考单词 Word It优先顺序小升初单词 对你学习有帮助的其他畅销书目推荐 阿豆拜师 这样阅读拿高分 高中现代文（第2版） 阿豆拜师 这样阅读拿高分 高中文言文 阿豆拜师 记住这些拿高分 高中文言文 按照高考考频顺序记忆的单词书 Strategy: 按照近年来高考中出现单词的重要程度收录 Useful: 大量收录了高考中出现的句子，提高实战感 Speed: 运用图片、联想、词根、比较等方法征服词汇 Easy to Memory: 融入三阶段反复练习模式，完美词汇记忆 Index：迅速找出你想学习的单词的位置 **版销售火爆，得到读者广泛好评！', 'images/goods/a9b97c3b8fd74fce8a1d3d1e83683f02.jpg', 152, 30.00, 30.00, '2019-05-17 02:51:54', NULL, 1);
