INSERT INTO `mall_goods` VALUES (47, 'IT资源原理', '6958191580139', '（美）拉夫曼 主编 ', '西南交通大学出版社', '2012-11-01 00:00:00', 1, '', 'images/goods/f4d8d25434c242dfbb5fd9831fa51448.jpg', 455, 59.80, 41.20, '2020-03-29 13:49:36', NULL, 1);
INSERT INTO `mall_goods` VALUES (48, 'IT狂人-英式幽默.谁的心里不住着一个疯子(含光盘)', '7215722765836', '李飞主编 ', '中国宇航出版社', '2015-01-01 00:00:00', 1, '这些年来，看英剧，学英语被认为是学习英语*好的途径之一。英剧的画面、发音、语言组合等创造了一种学习英语的 多媒体教室 ，让众多观看英剧的人如同身临其境，咀嚼其发音，品味其词句，感受其情景，获得了视觉上、听觉上的强烈体验。所以，建立在对英剧热播深刻分析的基础上，《跟着英剧去旅行，优雅英音轻松练就》丛书应运而生。该丛书有以下五大特点。 一、精选情节感人、语言地道的情景片段。 精选精彩片段带你重温经典情节，复古、侦探、魔幻、幽默 二、发音重点全面解析。结合发声学、语音学、音系学，全面解析英语语音技巧，语音重点一网打尽，轻松练就令人惊艳的地道英音，全面打造你精准自信的英语表达，提升你的英语表达艺术；英剧看起来，你的语音，也能这样优雅。 三、精彩口语全场景放送。 精选*热门英剧中的*', 'images/goods/9f0c21b7d1c848a7bcfbb53310bddc80.jpg', 166, 34.80, 26.80, '2020-03-27 15:22:30', NULL, 1);
