INSERT INTO `mall_goods` VALUES (74, '大学IT教程 windows 7+office2010', '7252175724114', '王太雷，贝依林 著 ', '人民邮电出版社', '2015-09-01 00:00:00', 1, '本书是为非计算机专业（本科、专科）的计算机入门课程“大学IT教程”而编写的教材，取材新颖实用，面向教学过程，突出建立计算机的基本概念和培养实际操作的能力。', 'images/goods/79dbd2a91eff4193882b33a600c5f3a6.jpg', 572, 45.00, 37.60, '2019-09-19 20:41:25', NULL, 1);
