INSERT INTO `mall_goods` VALUES (125, 'It\'s Christmas, David! 大卫的圣诞节 9780545143110', '6235606575340', 'David Shannon 著 ', 'Scholastic', '2010-09-01 00:00:00', 1, 'David Shannon is the internationally acclaimed creator of more than thirty picture books, including No, David!, a Caldecott Honor Book and a New York Times Best Illustrated Book of the Year. In addition to two more David picture books and three David board books, Shannon\'s bestsellers include Too Many Toys; Good Boy, Fergus!; Alice the Fairy; and A Bad Case of Stripes. David Shannon and his family live in Southern California.', 'images/goods/49e70612006c41d286efc6b5ee3ea8c3.jpg', 905, 146.00, 78.80, '2019-10-10 16:01:55', NULL, 1);
INSERT INTO `mall_goods` VALUES (126, 'SCI论文写作和发表：You Can Do It(第二版) SCI论文写作和发表', '4255966115769', '［美］张俊东、杨亲正、国防 编著 ', '化学工业出版社', '2016-04-01 00:00:00', 1, '8000多篇SCI论文的经验总结 从写作到发表，全流程展示 若干实例和具体建议，手把手教你写论文 母语式写作，你也可以做到', 'images/goods/d6295e6905514bcfb33d6435092fe2df.jpg', 586, 35.00, 22.10, '2019-07-17 04:20:07', NULL, 1);
