INSERT INTO `mall_goods` VALUES (150, 'PHP 开发从入门到精通', '6406355284416', '袁鑫 主编，希赛IT教育研发中心组织 编写 ', '水利水电出版社', '2010-04-01 00:00:00', 1, '', 'images/goods/0e39a3530ec44d5fb7dc24664741da06.jpg', 818, 58.00, 44.70, '2019-07-25 12:13:37', NULL, 1);
INSERT INTO `mall_goods` VALUES (151, 'IT应用文写作（第2版） 本书针对提高理工科学生写作能力而编写，案例丰富，内容新颖，课件完备，教学设计严谨，具有鲜明的实践特色和良好的指导作用。', '4752591142905', '周苏 ', '清华大学出版社', '2017-01-01 00:00:00', 1, '对理工科（包括IT专业）的学生来说，应用文写作能力是十分重要的。本书基于社会实践的需求，为 IT应用文写作 和 科技应用文写作 课程全新设计编写，具有丰富的实践特色。本书重视理论性和实践性，以应用文写作为基础，结合科技应用文的要求和IT行业的建设需求，指导IT专业和科技应用文的写作实践。本书系统、全面地介绍了专业应用文写作的基本知识和技能，详细介绍了应用文写作基础、软件文档编制规范、规划计划类应用文、申请申报类应用文、招标投标类应用文、合同协议类应用文、报告说明类应用文、鉴定评议类应用文、学术论文与参考文献、文献综述以及毕业论文与答辩等内容，具有良好的指导和参考作用。结合课堂教学方法改革的要求，本书全新设计了课程教学过程，包括课前阅读、习作点评、案例研究、课程教学、课后习作和阅读思考等教', 'images/goods/76af7c6c1f104f659cdfbdb9f760372c.jpg', 383, 35.00, 35.00, '2019-05-26 03:12:27', NULL, 1);
