INSERT INTO `mall_goods` VALUES (641, '现代企业经营管理――理论与实验模拟', '8766078895741', '齐永兴，王景峰 主编 ', '经济科学出版社', '2015-02-01 00:00:00', 5, '齐永兴、王景峰主编的这本《现代企业经营管理--理论与实验模拟》分为两个部分：**部分主要针对企业经营管理过程当中所需要的主要管理理论与方法进行阐述。在企业整个经营管理过程中，通过对战略管理理论、营销管理、财务管理、人力资管理、运管管理等方面知识的学习，为管理类学生系统掌握及应用现代企业经营管理理论奠定基础；第二部分主要通过对当前两个经营决策模拟软件的介绍，以及上述经营管理理论在该软件的模拟应运，加深同学们对所学知识与理论的感性认识和动手能力。', 'images/goods/03df3424205f437b99bad05dfe67eb5d.jpg', 1070, 52.00, 41.00, '2019-03-11 18:08:50', NULL, 1);
