INSERT INTO `mall_goods` VALUES (646, '小企业经营管理一本通系列――小企业贷款一本通', '5437513163429', '邱俊如 主编 ', '浙江工商大学出版社', '2012-06-01 00:00:00', 5, '邱俊如编著的《小企业贷款一本通》书中所涉及的信贷产品以小企业为主，但也涉及农户、个体工商户等群体。因此，本书可以供有信贷需求的普通群众(包括农户、个体工商户、私营业主、小伞业主、创业大学生等)阅读，也可为小额信贷从业人员。小额信贷初学者和在校学生学习提供参考。', 'images/goods/b9ac59205e2e4a49a853449745e00ef0.jpg', 1031, 16.00, 12.60, '2020-02-09 03:32:41', NULL, 1);
