INSERT INTO `mall_goods` VALUES (650, '格力的经营管理智慧：格力非常道', '8450270981192', '陈宗麟、周锡冰 ', '广东经济出版社有限公司', '2016-05-01 00:00:00', 5, '陈宗麟、周锡冰*的《格力的经营管理智慧 格力非常道》从7大角度分析格力成功的秘密，剖析格力面临的3大难题探讨格力的未来定位。 营销制胜：营销为何能制胜 品牌至上：品牌就是使消费者形成思维定势 管理无极限：专业化成就全球**企业 贴近顾客心：唯有基于品质的服务才能真正赢得顾客 征战海外：稳健是国际化战略的成功之本 对峙渠道商：消费者的利益才是*大的利益 面临三大难题：格力还需进一步努力 格力未来路：**之后的战略选择', 'images/goods/45b57d9597994341939e7a45525ad159.jpg', 862, 22.00, 16.50, '2019-09-11 05:07:54', NULL, 1);
