INSERT INTO `mall_goods` VALUES (669, '六五普法企事业经营管理人员读本', '8110555773444', '本社编写组 编 ', '国家行政学院出版社', '2011-06-01 00:00:00', 5, '本书根据中共中央、国务院转发《中央宣传部、司法部关于在公民中展开法制宣传教育的第六个五年规划》的通知而编写，内容涉及与公民切身利益攸关的法律法规，采用本章概述、法规解析、本章测试题的体例，书后附有测试题。既可普及法律法规常识，又可检验学习效果，二者相得益彰，是各级各类人员开展六五普法的权威辅导读物。', 'images/goods/18e7142ad27a4eae9a99eddbd97c03f1.jpg', 592, 28.00, 19.30, '2019-06-19 23:16:19', NULL, 1);
