INSERT INTO `mall_goods` VALUES (679, '经济型酒店经营管理全解 为您提供经济型酒店从选址、前期准备，到日常管理，再到突发事件处理等一系列的方法和技巧，内容详细全面，非常实用！', '2613666557801', '薛永刚，王文燕 编著 ', '化学工业出版社', '2012-02-01 00:00:00', 5, '', 'images/goods/e7134644f775440ea3dba38cb7a46ecb.jpg', 373, 48.00, 35.90, '2020-03-04 23:09:50', NULL, 1);
INSERT INTO `mall_goods` VALUES (680, '农场经营管理学', '5273138040055', '姜法竹 等编著 ', '科学出版社', '2015-10-01 00:00:00', 5, '姜法竹、翟绪军、甫永民、刘秀岩、韩光鹤等编*的《农场经营管理学》从农场经营与管理的内涵入手，在第1章、第2章分别介绍了农场历史发展与变革；第3章针对农场土地经营制度、农场土地承包经营以及农场土地资源综合利用进行了详细的阐释；第4章重点对农场农业生产管理中各重点环节进行介绍；第5章对农业机械作业、农业机械操作人员管理、农机基础设施管理等管理进行介绍；第6章介绍了农场人力资源管理的农场人力资源规划、农场人力资源的招聘与甄别、农场员工培训等；第7章对农场会计与财务管理的国有农场会计、国有农场财务管理等阐述；第8章对农场二、三产企业经营管理的农场农产品加工业生产管理、农场农产品流通业进行梳理；第9章对农场社会化管理，特别是农场社会化管理概述、农场思想文化建设等进行重点研究；第10章对农场文化，', 'images/goods/ba10bda7a76a445e9afd0bad86b19c8c.jpg', 684, 80.00, 63.10, '2019-03-08 01:27:39', NULL, 1);
