INSERT INTO `mall_goods` VALUES (689, '专利经营管理', '8443833042146', '袁真富 ', '知识产权出版社', '2011-01-01 00:00:00', 5, '由袁真富编著的《专利经营管理》立足于企业知识产权管理实际应用，结合我国企业知识产权工作的具体实践，以案说法，深入浅出，针对企业知识产权工作中面临．的实际问题，生动翔实地提出了切实可行的解决办法、建议和法律依据。相信这套书的出版，将有利于我国广大企业知识产权工作者提高运用有关知识分析问题、解决问题的能力，成为一套学有所得的实用教材。', 'images/goods/5736e41702b24bccb9698ac15f8f0827.jpg', 792, 48.00, 37.90, '2020-03-05 07:58:14', NULL, 1);
INSERT INTO `mall_goods` VALUES (690, '企业经营管理沙盘模拟实训教程(第二版） 基于ITMC电子和物理沙盘编写的企业经管管理沙盘模拟实训教程', '8500073294319', '邓文博、曾苑 主编 ', '化学工业出版社', '2016-01-01 00:00:00', 5, '1.作者所带的学生在沙盘大赛中多次获奖，经验丰富； 2.北京大学光华管理学院原党委书记、副院长王其文教授；中山大学创业学院教学总监任荣伟教授为本书作序。', 'images/goods/4a18a9f00202459f818203dd52f2f5be.jpg', 560, 29.80, 25.80, '2019-05-19 16:59:58', NULL, 1);
INSERT INTO `mall_goods` VALUES (691, '河南煤炭经营管理战略构思', '1207188715898', '陈富刚 ', '黄河水利出版社', '2014-11-01 00:00:00', 5, '', 'images/goods/28e1f29493404484933de0c248d25def.jpg', 919, 50.00, 39.50, '2019-07-29 02:05:44', NULL, 1);
INSERT INTO `mall_goods` VALUES (692, '新编酒店经营管理（酒店规范化管理、提高工作效率必备的工作实用工具书）', '0567149399522', '梭伦主编 肖云山编著 ', '江苏美术出版社', '2013-06-01 00:00:00', 5, '本书就酒店业的经营、管理、财务预算、人力资源、员工培训与流动等方面的问题做了较深入的比较和分析，并提出了一些对策。', 'images/goods/e1e7502b9376480286c753357b68e8ec.jpg', 199, 32.00, 22.00, '2019-05-12 10:39:22', NULL, 1);
