INSERT INTO `mall_goods` VALUES (695, '茶叶企业经营管理', '3702428340878', '杨江帆 著 ', '中国农业出版社', '2006-07-01 00:00:00', 5, '', 'images/goods/d93de2817d5b4e3aa5b6b4f88cb19fdf.jpg', 858, 32.50, 25.60, '2019-08-29 14:58:35', NULL, 1);
INSERT INTO `mall_goods` VALUES (696, '特许经营管理', '5959125021422', '赵敬明 主编 ', '中国财政经济出版社一', '2014-09-01 00:00:00', 5, '', 'images/goods/3a3be14ce9dc4491b0b97900d8e2cd9a.jpg', 779, 34.00, 26.00, '2019-04-13 04:29:13', NULL, 1);
INSERT INTO `mall_goods` VALUES (697, '现代传媒经营管理(屠忠俊) 高职高专教育法律类专业教学改革试点与推广教材', '4167280953521', '屠忠俊 ', '华中科技大学出版社', '2019-09-01 00:00:00', 5, '本书总结了作者20多年来从事传媒经营管理教学、科研的工作成果，体例严谨、体系完备、内容扎实。 在严密界定媒介、媒体、传媒的基础上，本书结合传媒经营管理的实际，阐述了管理经济学的相关基本内容，并就中国的传媒经营管理政策进行了分析，从经济、政治两个方面说明了中国传媒经营管理的理论依据和规制前提。本着宜分则分、宜合则合的原则，本书对报纸、广播、电视、因特网、移动通信等类媒介的经营管理业务工作 技术改造、生产(按传媒双重出售的特色划分为传媒产品制作和广告发布两个基本面)、营销、财务、人力资源、公共关系和组织设计等 进行了系统的阐述，并就它们在媒体中的实际运作进行了说明；在此过程中，介绍并运用了一系列经营管理的新理论与新方法。', 'images/goods/929b6d7c7f0b423ba6fe0c851df8c8d1.jpg', 812, 68.00, 57.80, '2019-12-27 17:10:04', NULL, 1);
INSERT INTO `mall_goods` VALUES (698, '农村经营管理培训教材', '9587461872252', '白剑,任义琴 ', '山西经济出版社', '2017-09-01 00:00:00', 5, '农村经营管理培训教材', 'images/goods/efeb48f9e7e94e2083b0a5bd9a1cdc95.jpg', 482, 28.00, 19.30, '2019-08-30 05:45:24', NULL, 1);
INSERT INTO `mall_goods` VALUES (699, '企业经营管理基础', '3047994751303', '闻学，赵华明 主编 ', '中国劳动社会保障出版社', '2009-09-01 00:00:00', 5, '本书模拟中小型生产企业，以其采购、生产、销售、仓储的生产经营环节为主线，附以人力资源、企业绩效评价、企业战略等模块，以任务的形式来组织教学内容。本书在介绍管理知识的同时，还介绍了企业生产经营和管理中各个环节发生的单据及其传递流程，帮助学生了解企业生产经营与财务活动联系，了解各个环节成本控制的方法。本书的管理知识结合了大量的实例，好学易懂。', 'images/goods/00b5a81cc4ce494f870f4d80bffa774f.jpg', 208, 19.00, 18.60, '2019-06-18 19:54:06', NULL, 1);
INSERT INTO `mall_goods` VALUES (700, '旅行社经营管理（耿红莉）', '6285929799199', '耿红莉 主编 ', '化学工业出版社', '2019-08-01 00:00:00', 5, '1.《旅行社经营管理》一书采用 任务驱动 的编写思路，以旅行社典型工作任务为项目载体，从任务引入、任务分析到相关知识的学习，再到任务解决，较好地体现了理论和实践的结合、知识和行动的协调。 2.《旅行社经营管理》一书由具有丰富教学经验的学校老师和多年旅行社从业经历的同行合作编写，书中的案例和相关表格都是相关旅行社正在使用的一手资料，对学习者有很好的参考和借鉴意义。', 'images/goods/abc2d8fe965b4733b39c2642623bc02a.jpg', 119, 36.00, 31.20, '2020-02-02 12:02:28', NULL, 1);
