INSERT INTO `mall_goods` VALUES (703, '家庭农场经营管理', '2660083784194', '李明慧，周承波 主编 ', '中国农业科学技术出版社有限公司', '2015-08-01 00:00:00', 5, '', 'images/goods/228f2a058b98499a8ca854fdc088076d.jpg', 467, 28.00, 21.00, '2020-02-26 20:27:04', NULL, 1);
INSERT INTO `mall_goods` VALUES (704, '跨国公司经营管理案例：世界500强企业的成功之道（卓越・21世纪管理学系列）', '7289332618682', '郭伟，徐翔 主编 ', '复旦大学出版社', '2012-02-01 00:00:00', 5, '适合作为高职高专院校和实践型本科院校教材，也可作为各类相关培训的教材以及从业人员参考书。 以复旦大学为主的研究型大学、上海商学院等十几家实践型院校以及有影响的企业充分发挥各自理论与实践的特长，联合编写。 注重教材的实践性、实用性，以及对实际岗位的适应性，学生学后就能进入实际操作。 滚动开发，逐步覆盖管理学各门课程。 全新体例与版式，教辅合一，正文、教师指导、案例、习题**结合，以方便教学为目标。', 'images/goods/9402e32b669f426faba6eaae828dfd3d.jpg', 225, 28.00, 26.30, '2019-11-10 10:48:25', NULL, 1);
INSERT INTO `mall_goods` VALUES (705, '连锁经营管理', '0506193059106', '彭纯宪 主编 ', '机械工业出版社', '2011-07-01 00:00:00', 5, '', 'images/goods/1df3b6f2ea464cf1af080ffe698769e3.jpg', 857, 24.00, 18.90, '2019-04-12 14:27:05', NULL, 1);
INSERT INTO `mall_goods` VALUES (706, '银行经营管理学', '4069210248166', '陈晶萍，张玉喜 主编 ', '哈尔滨工程大学出版社', '2007-03-01 00:00:00', 5, '本书是为适应金融专业教学的需要，为培养熟悉和掌握商业银行经营管理的业务知识、理论方法的人才而编写的。全书主要介绍和探讨了市场经济下商业银行经营管理的理论、方法、技术及各项业务。内容包括：商业银行的性质与职能，商业银行的经营管理，商业银行的经营管理理论和方法，商业银行的资本管理、负债业务、资产业务、证券投资业务等。本书体系结构合理、紧凑，基本知识完整、系统，并能够反映商业银行经营管理实践中的新成果、新规则。', 'images/goods/4ba7d64571164bc69de70187baceb1c7.jpg', 969, 21.00, 16.50, '2019-01-12 07:15:37', NULL, 1);
